import React from "react";
import styles from "./StockOverview.module.css";

// StockItem component for each grid item
const StockItem = ({ icon, label, value, progressColor, backgroundColor }) => {
  return (
    <div className={styles.stockItem}>
      <div className={styles.iconContainer} style={backgroundColor={backgroundColor}}>{icon}</div>
      <div className={styles.contentContainer}>
        <div className={styles.label}>{label}</div>
        <div className={styles.progressBarContainer}>
          <div
            className={styles.progressBar}
            style={{ backgroundColor: progressColor }}
          />
        </div>
      </div>
      <div className={styles.value}>{value}</div>
    </div>
  );
};

